tables.table_schema:
CREATE TABLE wind_monitoring (
    altitude INT,
    wind_speed FLOAT,
    wind_direction VARCHAR(20)
);

CREATE TABLE WindData (
  altitude INT,
  wind_speed FLOAT,
  wind_direction VARCHAR
);

CREATE TABLE AltitudeInfo (
  altitude_id SERIAL PRIMARY KEY,
  altitude_name VARCHAR
);

CREATE TABLE WindSpeedAtAltitude (
  altitude INT,
  wind_speed FLOAT,
  direction VARCHAR
) PRIMARY KEY (altitude);

CREATE TABLE AltitudeLocations (
  altitude INT,
  location_name VARCHAR,
  country VARCHAR
) PRIMARY KEY (altitude);

CREATE TABLE AltitudeLevels (
    altitude_id UUID PRIMARY KEY,
    altitude_name VARCHAR(50) NOT NULL
);

CREATE TABLE WindData (
    wind_id UUID PRIMARY KEY,
    altitude_id UUID REFERENCES AltitudeLevels(altitude_id),
    wind_speed FLOAT NOT NULL,
    wind_direction VARCHAR(10) NOT NULL
);

CREATE TABLE Device (
    device_id UUID PRIMARY KEY,
    device_name VARCHAR(50) NOT NULL
);

CREATE TABLE altitude_data (
  altitude_id SERIAL PRIMARY KEY,
  altitude_name TEXT,
  wind_speed FLOAT,
  wind_direction TEXT
);

CREATE TABLE location_data (
  location_id SERIAL PRIMARY KEY,
  location_name TEXT,
  latitude FLOAT,
  longitude FLOAT
);

CREATE TABLE sensor_data (
  sensor_id SERIAL PRIMARY KEY,
  sensor_name TEXT,
  sensor_type TEXT
);

CREATE TABLE sensor_measurement (
  sensor_measurement_id SERIAL PRIMARY KEY,
  sensor_id INT REFERENCES sensor_data(sensor_id),
  measurement_id INT REFERENCES measurement_data(measurement_id),
  sensor_value FLOAT
);
