tables.table_schema:
CREATE TABLE UserLocations (
  user_id INT PRIMARY KEY,
  user_name VARCHAR(50),
  user_location VARCHAR(100),
  last_alert_timestamp TIMESTAMP
);

CREATE TABLE weather_alerts (
    alert_id SERIAL PRIMARY KEY,
    alert_type VARCHAR(50) NOT NULL,
    alert_message TEXT NOT NULL,
    alert_time TIMESTAMP NOT NULL
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE user_notifications (
    notification_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    alert_id INT REFERENCES weather_alerts(alert_id),
    notification_time TIMESTAMP NOT NULL
);

CREATE TABLE alerts_history (
    history_id SERIAL PRIMARY KEY,
    alert_id INT REFERENCES weather_alerts(alert_id),
    notification_id INT REFERENCES user_notifications(notification_id),
    history_time TIMESTAMP NOT NULL
);

CREATE TABLE alert_categories (
	c_id SERIAL PRIMARY KEY,
	c_name VARCHAR(50) NOT NULL,
	c_description TEXT
);

CREATE TABLE alert_subscribers (
	s_id SERIAL PRIMARY KEY,
	s_user_id INT NOT NULL,
	s_category_id INT NOT NULL
);
