CREATE TABLE WeatherData (
    location_id INT PRIMARY KEY,
    temperature FLOAT,
    humidity FLOAT,
    precipitation FLOAT
);

CREATE TABLE LocationInfo (
    location_id INT PRIMARY KEY,
    city VARCHAR(50),
    state VARCHAR(50),
    country VARCHAR(50)
);

CREATE TABLE Forecast (
    forecast_id INT PRIMARY KEY,
    location_id INT,
    date DATE,
    forecast_text TEXT
);

