tables.table_schema:
CREATE TABLE QualityControlData (
  product_id INT PRIMARY KEY,
  product_name STRING,
  production_date DATE,
  inspection_result STRING
);

CREATE TABLE IndustryStandards (
  standard_id INT PRIMARY KEY,
  standard_name STRING,
  minimum_requirement FLOAT,
  compliance_status STRING
);

CREATE TABLE Regulations (
  regulation_id INT PRIMARY KEY,
  regulation_name STRING,
  description TEXT,
  enforcement_date DATE
);

CREATE TABLE inspectors (
    inspector_id INT PRIMARY KEY,
    inspector_name VARCHAR(255),
    inspector_email VARCHAR(255),
    supervisor_id INT
);

CREATE TABLE standards (
    standard_id INT PRIMARY KEY,
    standard_name VARCHAR(255),
    description TEXT,
    last_update DATE
);

CREATE TABLE Employees (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(255),
    department VARCHAR(50),
    hire_date DATE
);

CREATE TABLE Departments (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(50)
);

CREATE TABLE Regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(100),
    description TEXT
);
