tables.table_schema:
CREATE TABLE energy_consumption_monitoring (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(50),
    energy_usage DECIMAL,
    cost_saving_initiative BOOLEAN
);

CREATE TABLE EnergyConsumption (
    facility_id INT PRIMARY KEY,
    energy_usage INT,
    timestamp TIMESTAMP,
    cost_saved FLOAT
);

CREATE TABLE FacilityDetails (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(255),
    location VARCHAR(255),
    manager_id INT
);

CREATE TABLE facility_locations (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE cost_saving_initiatives (
    initiative_id INT PRIMARY KEY,
    initiative_name VARCHAR(255) NOT NULL,
    savings_estimate DECIMAL(10, 2) NOT NULL,
    implementation_date DATE NOT NULL
);
