tables.table_schema:
CREATE TABLE machine_data (
 machine_id INT PRIMARY KEY,
 timestamp TIMESTAMP,
 temperature INT,
 vibration_level INT
);

CREATE TABLE machine_data (
  id INT PRIMARY KEY,
  machine_id INT,
  sensor_data JSON,
  timestamp TIMESTAMP
);

CREATE TABLE maintenance_records (
  id INT PRIMARY KEY,
  machine_id INT,
  maintenance_type VARCHAR(50),
  maintenance_date DATE
);

CREATE TABLE machine_data (
  id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  sensor_id INT,
  reading FLOAT
);

CREATE TABLE sensor (
  sensor_id INT PRIMARY KEY,
  location VARCHAR(50),
  model VARCHAR(50)
);

CREATE TABLE maintenance_history (
  maintenance_id INT PRIMARY KEY,
  machine_id INT,
  maintenance_date DATE,
  description TEXT
);

CREATE TABLE Machines (
    machine_id UUID PRIMARY KEY,
    machine_name STRING,
    location STRING
);

CREATE TABLE PredictiveModels (
    model_id UUID PRIMARY KEY,
    model_name STRING,
    fields_required STRING[],
    model_accuracy FLOAT
);
