tables.table_schema:
CREATE TABLE MotionDetectors (
  id INT PRIMARY KEY,
  location VARCHAR(255),
  status VARCHAR(50)
);

CREATE TABLE Cameras (
  id INT PRIMARY KEY,
  location VARCHAR(255),
  brand VARCHAR(50),
  resolution VARCHAR(20)
);

CREATE TABLE SensorData (
  id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  sensor_id INT,
  reading FLOAT
);

CREATE TABLE motion_detectors (
  id INT PRIMARY KEY,
  sensor_id INT,
  location VARCHAR(50),
  motion_status BOOLEAN
);

CREATE TABLE cameras (
  id INT PRIMARY KEY,
  camera_id INT,
  location VARCHAR(50),
  camera_status BOOLEAN
);

CREATE TABLE sensor_data (
  id INT PRIMARY KEY,
  sensor_id INT,
  timestamp TIMESTAMP,
  reading FLOAT
);

CREATE TABLE camera_data (
  id INT PRIMARY KEY,
  camera_id INT,
  timestamp TIMESTAMP,
  image_url VARCHAR(255)
);
