tables.table_schema:
CREATE TABLE patient (id INT PRIMARY KEY,name VARCHAR(50) NOT NULL,age INT,gender VARCHAR(10),phone_number VARCHAR(15));
CREATE TABLE medication (id INT PRIMARY KEY,name VARCHAR(50) NOT NULL,dosage INT,frequency VARCHAR(20),start_date DATE);
CREATE TABLE wearable_device (id INT PRIMARY KEY,model VARCHAR(50) NOT NULL,manufacturer VARCHAR(50),serial_number VARCHAR(20),purchase_date DATE);
CREATE TABLE dosage_history (id INT PRIMARY KEY,patient_id INT NOT NULL,medication_id INT NOT NULL,dosage_taken INT,timestamp TIMESTAMP);
