tables.table_schema:
CREATE TABLE SleepData (
    user_id INT PRIMARY KEY,
    sleep_start TIMESTAMP,
    sleep_end TIMESTAMP,
    sleep_quality INT
);

CREATE TABLE SleepRecommendations (
    user_id INT PRIMARY KEY,
    recommendation_text TEXT,
    recommendation_date DATE,
    recommendation_status VARCHAR
);

CREATE TABLE user_data (
    user_id UUID PRIMARY KEY,
    sleep_duration INT,
    deep_sleep_duration INT,
    light_sleep_duration INT
);

CREATE TABLE sleep_quality (
    user_id UUID,
    date DATE,
    quality_rating INT,
    FOREIGN KEY (user_id) REFERENCES user_data(user_id)
);

CREATE TABLE wearable_devices (
    device_id SERIAL PRIMARY KEY,
    device_name VARCHAR(50),
    user_id UUID,
    FOREIGN KEY (user_id) REFERENCES user_data(user_id)
);

CREATE TABLE sleep_recommendations (
    recommendation_id SERIAL PRIMARY KEY,
    user_id UUID,
    recommendation_text TEXT,
    FOREIGN KEY (user_id) REFERENCES user_data(user_id)
);

CREATE TABLE sleep_patterns (
  user_id INT NOT NULL,
  date DATE NOT NULL,
  duration_minutes INT NOT NULL
);

CREATE TABLE deep_sleep (
  user_id INT NOT NULL,
  date DATE NOT NULL,
  duration_minutes INT NOT NULL
);

CREATE TABLE light_sleep (
  user_id INT NOT NULL,
  date DATE NOT NULL,
  duration_minutes INT NOT NULL
);

CREATE TABLE sleep_quality (
  user_id INT NOT NULL,
  date DATE NOT NULL,
  quality_score INT NOT NULL
);

CREATE TABLE sleep_recommendations (
  user_id INT NOT NULL,
  date DATE NOT NULL,
  recommendation TEXT
);
