tables.table_schema:
CREATE TABLE Temperature_Log (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    temperature FLOAT
);

CREATE TABLE Humidity_Log (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    humidity FLOAT
);

CREATE TABLE temperature_data (
  sensor_id UUID,
  timestamp TIMESTAMP,
  temperature FLOAT
);

CREATE TABLE humidity_data (
  sensor_id UUID,
  timestamp TIMESTAMP,
  humidity FLOAT
);

CREATE TABLE temperature_log (
  id SERIAL PRIMARY KEY,
  timestamp TIMESTAMPTZ NOT NULL,
  location TEXT NOT NULL,
  temperature FLOAT NOT NULL
);

CREATE TABLE humidity_log (
  id SERIAL PRIMARY KEY,
  timestamp TIMESTAMPTZ NOT NULL,
  location TEXT NOT NULL,
  humidity FLOAT NOT NULL
);

CREATE TABLE climate_settings (
  id SERIAL PRIMARY KEY,
  temperature_threshold FLOAT NOT NULL,
  humidity_threshold FLOAT NOT NULL
);

CREATE TABLE climate_control_system (
  id SERIAL PRIMARY KEY,
  control_number INT NOT NULL,
  status TEXT NOT NULL
);

CREATE TABLE Temperature_Logs (
    id SERIAL PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    temperature FLOAT NOT NULL
);

CREATE TABLE Humidity_Logs (
    id SERIAL PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    humidity FLOAT NOT NULL
);

CREATE TABLE Climate_Zones (
    zone_id SERIAL PRIMARY KEY,
    zone_name VARCHAR(50) NOT NULL
);

CREATE TABLE Climate_Zone_Temperature (
    zone_id INT,
    temperature FLOAT NOT NULL,
    CONSTRAINT fk_zone FOREIGN KEY (zone_id) REFERENCES Climate_Zones(zone_id)
);

CREATE TABLE Climate_Zone_Humidity (
    zone_id INT,
    humidity FLOAT NOT NULL,
    CONSTRAINT fk_zone FOREIGN KEY (zone_id) REFERENCES Climate_Zones(zone_id)
);
