tables.table_schema:
CREATE TABLE crop_growth_tracking (
    crop_id UUID PRIMARY KEY,
    growth_stage TEXT,
    sensor_data JSONB,
    timestamp TIMESTAMP
);

CREATE TABLE crop_growths (
    crop_id INT PRIMARY KEY,
    growth_stage VARCHAR(50),
    sensor_data JSON,
    timestamp TIMESTAMP
);

CREATE TABLE sensor_data (
    sensor_id INT PRIMARY KEY,
    sensor_type VARCHAR(50),
    sensor_location VARCHAR(100),
    installation_date DATE
);

CREATE TABLE crop_sensors (
    crop_id INT,
    sensor_id INT,
    FOREIGN KEY (crop_id) REFERENCES crop_growths(crop_id),
    FOREIGN KEY (sensor_id) REFERENCES sensor_data(sensor_id)
);

CREATE TABLE sensor_location (
    sensor_id INT PRIMARY KEY,
    location VARCHAR(255),
    latitude DECIMAL,
    longitude DECIMAL
);

CREATE TABLE crop (
    crop_id UUID PRIMARY KEY,
    crop_name VARCHAR(100),
    planting_date TIMESTAMP,
    harvest_date TIMESTAMP
);

CREATE TABLE sensor (
    sensor_id UUID PRIMARY KEY,
    sensor_type VARCHAR(50),
    location VARCHAR(100),
    installation_date TIMESTAMP
);

CREATE TABLE growth_stage (
    stage_id UUID PRIMARY KEY,
    stage_name VARCHAR(50),
    description TEXT
);
