tables.table_schema:
CREATE TABLE soil_moisture_data (
  sensor_id INT PRIMARY KEY,
  sensor_location VARCHAR,
  moisture_level DECIMAL
);

CREATE TABLE irrigation_needs (
  plant_id INT PRIMARY KEY,
  plant_name VARCHAR,
  irrigation_amount DECIMAL
);

CREATE TABLE SoilMoistureReadings (
    sensor_id INT PRIMARY KEY,
    reading_time TIMESTAMP,
    moisture_level DECIMAL
);

CREATE TABLE SoilSensors (
    sensor_id INT PRIMARY KEY,
    sensor_location VARCHAR,
    sensor_type VARCHAR
);

CREATE TABLE SoilSensorData (
    sensor_id UUID,
    sensor_location VARCHAR,
    moisture_reading FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE WeatherData (
    weather_id UUID,
    temperature FLOAT,
    humidity FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE IrrigationSchedule (
    schedule_id UUID,
    irrigation_zone VARCHAR,
    start_time TIMESTAMP,
    end_time TIMESTAMP
);

CREATE TABLE IrrigationSettings (
    settings_id UUID,
    irrigation_zone VARCHAR,
    threshold_moisture FLOAT,
    max_watering_time INTERVAL
);
