tables.table_schema:
CREATE TABLE planting_schedule (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    planting_date DATE,
    harvest_date DATE
);

CREATE TABLE crop_yield (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    yield_amount FLOAT,
    yield_date DATE
);

CREATE TABLE Crop_Mix (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(255) NOT NULL,
    planting_date DATE NOT NULL,
    yield_per_acre DECIMAL(10,2) NOT NULL
);

CREATE TABLE Soil_Composition (
    composition_id INT PRIMARY KEY,
    soil_type VARCHAR(255) NOT NULL,
    pH_level DECIMAL(4,2) NOT NULL,
    nutrients TEXT
);
