tables.table_schema:
CREATE TABLE pest_types (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50),
    description TEXT
);

CREATE TABLE pests (
    pest_id INT PRIMARY KEY,
    pest_name VARCHAR(255),
    infestation_level INT
);

CREATE TABLE sensors (
    sensor_id INT PRIMARY KEY,
    location VARCHAR(255),
    installation_date DATE
);

CREATE TABLE infestation_patterns (
    pattern_id INT PRIMARY KEY,
    pest_id INT,
    timestamp TIMESTAMP,
    correlation_score FLOAT
);
