tables.table_schema:
CREATE TABLE livestock_health_monitoring ( 
    id INT PRIMARY KEY, 
    animal_id INT, 
    heart_rate INT, 
    activity_level INT 
);

CREATE TABLE LivestockHealth (
    id INT PRIMARY KEY,
    animal_id INT,
    heart_rate FLOAT,
    activity_level FLOAT
);

CREATE TABLE SensorData (
    sensor_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    animal_id INT,
    heart_rate FLOAT,
    activity_level FLOAT
);

CREATE TABLE livestock_health_data (
	heart_rate INT,
	activity_level INT
);

CREATE TABLE livestock_details (
	livestock_id INT,
	livestock_type VARCHAR(50),
	livestock_age INT
);

CREATE TABLE sensor_data (
	sensor_id INT,
	heart_rate INT,
	activity_level INT,
	timestamp TIMESTAMP
);

CREATE TABLE veterinarians (
	vet_id INT,
	vet_name VARCHAR(50),
	vet_specialty VARCHAR(50)
);

CREATE TABLE livestock_health (
  id INT PRIMARY KEY,
  livestock_id INT,
  heart_rate INT,
  activity_level INT
);

CREATE TABLE livestock (
  id INT PRIMARY KEY,
  name VARCHAR(50),
  species VARCHAR(50),
  age INT
);

CREATE TABLE sensors (
  id INT PRIMARY KEY,
  sensor_id INT,
  sensor_type VARCHAR(50),
  location VARCHAR(50)
);

CREATE TABLE locations (
  id INT PRIMARY KEY,
  location_id INT,
  latitude FLOAT,
  longitude FLOAT
);
