tables.table_schema:
CREATE TABLE CropYieldForecasting (
    id SERIAL PRIMARY KEY,
    sensor_id INT,
    weather_condition VARCHAR,
    soil_nutrients VARCHAR
);

CREATE TABLE CropYieldData (
    crop_id UUID,
    date DATE,
    weather_conditions JSON,
    soil_nutrients JSON
);

CREATE TABLE WeatherData (
    date DATE,
    temperature FLOAT,
    humidity FLOAT,
    precipitation FLOAT
);

CREATE TABLE SoilNutrientsData (
    date DATE,
    nitrogen FLOAT,
    phosphorus FLOAT,
    potassium FLOAT
);

CREATE TABLE weather_data(
    id INT PRIMARY KEY,
    temperature FLOAT,
    humidity FLOAT,
    precipitation FLOAT
);

CREATE TABLE soil_nutrients(
    id INT PRIMARY KEY,
    nitrogen FLOAT,
    phosphorus FLOAT,
    potassium FLOAT
);

CREATE TABLE crop_data(
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    crop_type VARCHAR(255),
    yield_prediction FLOAT
);

CREATE TABLE yield_forecast(
    id INT PRIMARY KEY,
    crop_id INT,
    forecast_date DATE,
    yield_forecast FLOAT
);
