tables.table_schema:
CREATE TABLE equipment_tracking (
    id SERIAL PRIMARY KEY,
    equipment_id INT,
    timestamp TIMESTAMP,
    latitude DECIMAL,
    longitude DECIMAL
);

CREATE TABLE machinery (
    id SERIAL PRIMARY KEY,
    machinery_id INT,
    machinery_type VARCHAR(50),
    last_known_location VARCHAR(100)
);

CREATE TABLE farm_machinery (
    machinery_id INT PRIMARY KEY,
    gps_location VARCHAR,
    last_update TIMESTAMP
);

CREATE TABLE machinery_activity (
    activity_id INT PRIMARY KEY,
    machinery_id INT,
    activity_time TIMESTAMP,
    activity_type VARCHAR
);

CREATE TABLE machinery_history (
    history_id INT PRIMARY KEY,
    machinery_id INT,
    timestamp TIMESTAMP,
    location VARCHAR
);

CREATE TABLE machinery (
    id SERIAL PRIMARY KEY,
    machinery_name VARCHAR(255),
    manufacturer VARCHAR(255),
    year INT
);

CREATE TABLE machinery_location (
    id SERIAL PRIMARY KEY,
    machinery_id INT,
    location_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE sensor (
    id SERIAL PRIMARY KEY,
    sensor_type VARCHAR(255),
    installation_date DATE
);
