tables.table_schema:
CREATE TABLE schedules (
    schedule_id INT PRIMARY KEY,
    schedule_name VARCHAR(255),
    start_time TIME,
    end_time TIME
);

CREATE TABLE routines (
    routine_id INT PRIMARY KEY,
    routine_name VARCHAR(255),
    device_type VARCHAR(50),
    action VARCHAR(50)
);

CREATE TABLE devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(255),
    device_type VARCHAR(50)
);

CREATE TABLE schedules_schedule (
    id INT PRIMARY KEY,
    name VARCHAR,
    start_time TIMESTAMP,
    end_time TIMESTAMP
);

CREATE TABLE routines_routine (
    id INT PRIMARY KEY,
    name VARCHAR,
    description TEXT,
    frequency VARCHAR
);

CREATE TABLE lighting_devices (
    id INT PRIMARY KEY,
    device_name VARCHAR,
    device_type VARCHAR,
    brightness INT
);

CREATE TABLE blinds (
    id INT PRIMARY KEY,
    blind_name VARCHAR,
    room_id INT,
    status VARCHAR
);

CREATE TABLE schedule_routine_mapping (
    schedule_id INT,
    routine_id INT,
    PRIMARY KEY (schedule_id, routine_id)
);
