tables.table_schema:
CREATE TABLE real_time_traffic_data (
  vehicle_id UUID PRIMARY KEY,
  timestamp TIMESTAMP,
  traffic_speed INT,
  traffic_flow INT
);

CREATE TABLE traffic_analysis (
  analysis_id UUID PRIMARY KEY,
  timestamp TIMESTAMP,
  average_speed INT,
  peak_hour TIMESTAMP
);

CREATE TABLE traffic_data (
  id INT PRIMARY KEY,
  vehicle_id INT,
  timestamp TIMESTAMP,
  speed INT
);

CREATE TABLE vehicle_info (
  vehicle_id INT PRIMARY KEY,
  vehicle_type VARCHAR(50),
  manufacturer VARCHAR(50),
  model VARCHAR(50)
);

CREATE TABLE traffic_analytics (
  id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  congestion_level VARCHAR(20)
);
