tables.table_schema:
CREATE TABLE Soil_Moisture (
  id INT PRIMARY KEY,
  sensor_id INT,
  moisture_level FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE soil_moisture_data (
  id SERIAL PRIMARY KEY,
  sensor_id INT NOT NULL,
  moisture_level DECIMAL NOT NULL,
  timestamp TIMESTAMP NOT NULL
);

CREATE TABLE city_water_usage (
  id SERIAL PRIMARY KEY,
  city_name VARCHAR(255) NOT NULL,
  water_usage DECIMAL NOT NULL,
  timestamp TIMESTAMP NOT NULL
);

CREATE TABLE SoilMoistureLevels (
  id INT PRIMARY KEY,
  sensor_id INT,
  moisture_level DECIMAL,
  timestamp TIMESTAMP
);

CREATE TABLE SmartCitySensors (
  sensor_id INT PRIMARY KEY,
  location VARCHAR(255),
  type VARCHAR(50),
  status ENUM('active', 'maintenance', 'inactive')
);

CREATE TABLE WaterUsageHistory (
  id INT PRIMARY KEY,
  sensor_id INT,
  usage_amount DECIMAL,
  usage_date DATE
);
