tables.table_schema:
CREATE TABLE PowerUsageData (
  meter_id UUID PRIMARY KEY,
  timestamp TIMESTAMP NOT NULL,
  power_usage DECIMAL NOT NULL
);

CREATE TABLE smart_meters (
    meter_id INT PRIMARY KEY,
    location VARCHAR(255),
    power_usage DECIMAL(10, 2),
    timestamp TIMESTAMP
);

CREATE TABLE energy_rates (
    rate_id SERIAL PRIMARY KEY,
    start_date DATE,
    end_date DATE,
    rate DECIMAL(5,2)
);

CREATE TABLE energy_consumers (
    consumer_id SERIAL PRIMARY KEY,
    consumer_name VARCHAR(255),
    location VARCHAR(255),
    is_residential BOOLEAN
);

CREATE TABLE smart_meters (
    meter_id UUID PRIMARY KEY,
    location_id INT,
    meter_type VARCHAR(50),
    installation_date DATE
);

CREATE TABLE power_consumption (
    consumption_id UUID PRIMARY KEY,
    meter_id UUID,
    reading_time TIMESTAMP,
    power_usage FLOAT
);

CREATE TABLE buildings (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(100),
    building_address VARCHAR(150)
);

CREATE TABLE energy_providers (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(100),
    contact_email VARCHAR(100)
);

CREATE TABLE energy_consumers (
    consumer_id INT PRIMARY KEY,
    consumer_name VARCHAR(100),
    energy_usage FLOAT,
    provider_id INT
);
