CREATE TABLE smart_kitchen_appliances (
  appliance_id INT PRIMARY KEY,
  appliance_name VARCHAR(50) NOT NULL,
  appliance_type VARCHAR(50) NOT NULL,
  quantity_in_stock INT
);

CREATE TABLE inventory (
    appliance_id INT PRIMARY KEY,
    appliance_name VARCHAR(50),
    stock_quantity INT,
    last_ordered DATE
);
