tables.table_schema:
CREATE TABLE VehicleMetrics (
    vehicle_id INT,
    metric_name VARCHAR,
    metric_value FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE VehicleMetrics (
    vehicle_id INT PRIMARY KEY,
    engine_temperature FLOAT,
    fuel_level FLOAT,
    speed INT
);

CREATE TABLE EngineStatus (
    engine_id INT PRIMARY KEY,
    engine_temperature FLOAT,
    oil_pressure FLOAT,
    coolant_temperature FLOAT
);

CREATE TABLE FuelConsumption (
    consumption_id INT PRIMARY KEY,
    vehicle_id INT,
    fuel_used FLOAT,
    distance_traveled INT
);

CREATE TABLE IssueDetection (
    issue_id INT PRIMARY KEY,
    detection_time TIMESTAMP,
    vehicle_id INT,
    issue_description TEXT
);
