tables.table_schema:
CREATE TABLE driver_behavior ( 
   driver_id INT PRIMARY KEY, 
   speeding_events INT, 
   harsh_braking_events INT, 
   harsh_acceleration_events INT 
 );

CREATE TABLE driver_compliance (
   driver_id INT PRIMARY KEY, 
   hours_of_service INT, 
   logbook_violations INT, 
   drug_test_compliance BOOLEAN 
 );

CREATE TABLE drivers (
  driver_id INT PRIMARY KEY,
  driver_name VARCHAR(50) NOT NULL,
  license_number VARCHAR(20) UNIQUE,
  company_name VARCHAR(50) NOT NULL
);

CREATE TABLE driver(
	id INT PRIMARY KEY,
	name VARCHAR(50),
	license_number VARCHAR(20),
	driving_experience_years INT
);

CREATE TABLE vehicle(
	vehicle_id INT PRIMARY KEY,
	make VARCHAR(50),
	model VARCHAR(50),
	year INT
);

CREATE TABLE violation(
	violation_id INT PRIMARY KEY,
	trip_id INT,
	description TEXT,
	violation_date DATE
);

CREATE TABLE driver_info (
  driver_id INT PRIMARY KEY,
  driver_name VARCHAR(50) NOT NULL,
  license_number VARCHAR(20) UNIQUE,
  total_miles_driven FLOAT
);

CREATE TABLE violation (
  violation_id INT PRIMARY KEY,
  event_id INT REFERENCES speeding_events(event_id),
  violation_date DATE,
  violation_type VARCHAR(50)
);
