tables.table_schema:
CREATE TABLE traffic_patterns (
  location_id INT PRIMARY KEY,
  street_name VARCHAR(100),
  vehicle_count INT,
  average_speed FLOAT
);

CREATE TABLE traffic_data (
  id UUID PRIMARY KEY,
  date TIMESTAMP,
  location TEXT,
  speed FLOAT
);

CREATE TABLE city_population (
  city_id UUID PRIMARY KEY,
  city_name TEXT,
  population INT,
  area_sq_km FLOAT
);

CREATE TABLE traffic_data (
  date DATE,
  time TIME,
  street_name VARCHAR(50),
  traffic_volume INT
);

CREATE TABLE weather_data (
  date DATE,
  temperature FLOAT,
  precipitation FLOAT,
  wind_speed FLOAT
);

CREATE TABLE city_infrastructure (
  street_name VARCHAR(50),
  road_condition VARCHAR(50),
  street_lights BOOLEAN,
  traffic_signals BOOLEAN
);

CREATE TABLE traffic_data (
    plate_number VARCHAR(20),
    vehicle_type VARCHAR(50),
    entry_timestamp TIMESTAMP,
    exit_timestamp TIMESTAMP
);

CREATE TABLE road_network (
    road_id INT PRIMARY KEY,
    road_name VARCHAR(100),
    road_length DECIMAL(10, 2),
    road_type VARCHAR(50)
);

CREATE TABLE traffic_signals (
    signal_id INT PRIMARY KEY,
    signal_location VARCHAR(100),
    signal_status VARCHAR(20),
    last_updated TIMESTAMP
);

CREATE TABLE city_population (
    city_name VARCHAR(50),
    population INT,
    area_sq_km DECIMAL(10, 2),
    average_income DECIMAL(10, 2)
);

CREATE TABLE TrafficLights (
	id INT PRIMARY KEY,
	location VARCHAR(255),
	status VARCHAR(10),
	last_updated TIMESTAMP
);

CREATE TABLE CongestionLevels (
	id INT PRIMARY KEY,
	location VARCHAR(255),
	level VARCHAR(10),
	traffic_intensity INT
);

CREATE TABLE CityPlanning (
	id INT PRIMARY KEY,
	location VARCHAR(255),
	suitable_for_construction BOOLEAN,
	current_population INT
);
