tables.table_schema:
CREATE TABLE TransportationRoutes (
    route_id INT PRIMARY KEY,
    route_name VARCHAR(50),
    distance FLOAT,
    carbon_emissions FLOAT
);

CREATE TABLE Vehicles (
    vehicle_id INT PRIMARY KEY,
    vehicle_type VARCHAR(50),
    fuel_type VARCHAR(20),
    fuel_capacity FLOAT
);

CREATE TABLE vehicles (
    vehicle_id SERIAL PRIMARY KEY,
    vehicle_type VARCHAR(50) NOT NULL,
    fuel_type VARCHAR(50) NOT NULL,
    emissions_rating FLOAT NOT NULL
);

CREATE TABLE monitoring_stations (
    station_id SERIAL PRIMARY KEY,
    station_name VARCHAR(100) NOT NULL,
    location VARCHAR(100) NOT NULL,
    capacity INTEGER NOT NULL
);

CREATE TABLE emissions_report (
    report_id SERIAL PRIMARY KEY,
    report_date DATE NOT NULL,
    vehicle_id INTEGER NOT NULL,
    carbon_emission FLOAT NOT NULL
);

CREATE TABLE carbon_emissions (
    id UUID PRIMARY KEY,
    vehicle_id UUID,
    emission_amount FLOAT,
    measurement_date DATE
);

CREATE TABLE vehicles (
    vehicle_id UUID PRIMARY KEY,
    vehicle_type VARCHAR,
    fuel_type VARCHAR,
    manufacturer VARCHAR
);

CREATE TABLE emissions_standards (
    standard_id UUID PRIMARY KEY,
    standard_name VARCHAR,
    max_emission_limit FLOAT,
    description TEXT
);

CREATE TABLE emissions_history (
    id UUID PRIMARY KEY,
    vehicle_id UUID,
    emission_amount FLOAT,
    measurement_date DATE
);

CREATE TABLE vehicles_tracking (
    tracking_id UUID PRIMARY KEY,
    vehicle_id UUID,
    route_id UUID,
    timestamp TIMESTAMP
);
