tables.table_schema:
CREATE TABLE sensors (
   sensor_id INT PRIMARY KEY,
   location VARCHAR(50),
   water_quality VARCHAR(20),
   reading_time TIMESTAMP
);

CREATE TABLE WaterQualityReadings (
    location_id UUID,
    timestamp TIMESTAMP,
    pH_level FLOAT,
    turbidity FLOAT
);

CREATE TABLE SensorLocations (
    location_id UUID,
    location_name TEXT,
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE SensorStatus (
    sensor_id UUID,
    status TEXT,
    last_checkin TIMESTAMP
);

CREATE TABLE water_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(50) NOT NULL,
    location VARCHAR(100),
    sensor_id INT NOT NULL
);
