tables.table_schema:
CREATE TABLE SoilMoistureData (
    id INT PRIMARY KEY,
    date DATE,
    location VARCHAR(255),
    moisture_level INT
);

CREATE TABLE Soil_Moisture_Data (
  sensor_id INT PRIMARY KEY,
  moisture_level FLOAT NOT NULL,
  timestamp TIMESTAMP NOT NULL
);

CREATE TABLE Plant_Growth_Data (
  plant_id INT PRIMARY KEY,
  growth_rate FLOAT NOT NULL,
  last_watered TIMESTAMP
);
