tables.table_schema:
CREATE TABLE noise_levels (
	sensor_id INT,
	location VARCHAR(255),
	noise_level FLOAT,
	timestamp TIMESTAMP
);

CREATE TABLE UrbanAreas (
    id INT PRIMARY KEY,
    area_name VARCHAR(255),
    sensor_id INT,
    noise_level DECIMAL
);

CREATE TABLE Sensors (
    id INT PRIMARY KEY,
    sensor_name VARCHAR(255),
    location VARCHAR(255),
    installation_date DATE
);
