tables.table_schema:
CREATE TABLE sensors (
	sensor_id INT,
	sensor_type VARCHAR(50),
	manufacturer VARCHAR(50),
	deployed_date DATE
);

CREATE TABLE locations (
    location_id INT PRIMARY KEY,
    latitude DECIMAL,
    longitude DECIMAL,
    timestamp TIMESTAMP
);

CREATE TABLE sensors (
    sensor_id INT PRIMARY KEY,
    sensor_type VARCHAR(50),
    installed_location_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE gps_sensor_data (
    sensor_id INT PRIMARY KEY,
    sensor_model VARCHAR(50),
    battery_level FLOAT,
    last_updated TIMESTAMP
);

CREATE TABLE wildlife_species (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(50),
    category VARCHAR(50)
);

CREATE TABLE wildlife_habitat (
    habitat_id INT PRIMARY KEY,
    habitat_name VARCHAR(50),
    habitat_type VARCHAR(50)
);
