tables.table_schema:
CREATE TABLE SensorsData (
    sensor_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    sensor_type VARCHAR(50),
    sensor_value FLOAT
);

CREATE TABLE Anomalies (
    anomaly_id INT PRIMARY KEY,
    sensor_id INT,
    anomaly_description TEXT,
    detected_at TIMESTAMP
);

CREATE TABLE sensor_data (
    sensor_id INT PRIMARY KEY,
    data_time TIMESTAMP,
    sensor_value FLOAT
);

CREATE TABLE sensor_location (
    sensor_id INT PRIMARY KEY,
    location_name VARCHAR(255),
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE environmental_hazards (
    hazard_id INT PRIMARY KEY,
    hazard_type VARCHAR(255),
    hazard_location VARCHAR(255),
    detection_time TIMESTAMP
);
