tables.table_schema:
CREATE TABLE water_usage_data (
  user_id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  garden_zone_id INT,
  water_flow_rate FLOAT
);

CREATE TABLE water_usage_data (
  id INT PRIMARY KEY,
  date TIMESTAMP,
  liters_used INT
);

CREATE TABLE garden_devices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(50),
  garden_area FLOAT,
  is_active BOOLEAN
);

CREATE TABLE IF NOT EXISTS water_usage_logs (
  id UUID PRIMARY KEY,
  timestamp TIMESTAMP,
  device_id UUID,
  water_usage DECIMAL
);

CREATE TABLE IF NOT EXISTS devices (
  id UUID PRIMARY KEY,
  device_name VARCHAR,
  location VARCHAR,
  zone_id UUID
);

CREATE TABLE IF NOT EXISTS irrigation_zones (
  id UUID PRIMARY KEY,
  zone_name VARCHAR,
  zone_type VARCHAR,
  plant_type VARCHAR
);

CREATE TABLE WaterConsumption (
    timestamp TIMESTAMP,
    device_id UUID,
    amount DECIMAL
);

CREATE TABLE GardenDevices (
    device_id UUID,
    device_name STRING,
    device_type STRING
);

CREATE TABLE GardenAreas (
    area_id UUID,
    area_name STRING,
    area_size DECIMAL
);

CREATE TABLE GardenLocations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(255) NOT NULL
);

CREATE TABLE WaterConsumption (
    consumption_id INT PRIMARY KEY,
    location_id INT NOT NULL,
    consumption_date DATE NOT NULL,
    amount_used DECIMAL(8, 2) NOT NULL
);

CREATE TABLE SoilMoisture (
    moisture_id INT PRIMARY KEY,
    location_id INT NOT NULL,
    measurement_date DATE NOT NULL,
    moisture_level DECIMAL(5, 2) NOT NULL
);
