tables.table_schema:
CREATE TABLE firmware_updates (
    id INT PRIMARY KEY,
    device_id INT,
    firmware_version VARCHAR,
    update_date TIMESTAMP
);

CREATE TABLE security_patches (
    id INT PRIMARY KEY,
    patch_id INT,
    patch_description TEXT,
    patch_date TIMESTAMP
);

CREATE TABLE security_patches (
    id INT PRIMARY KEY,
    update_version VARCHAR(100) NOT NULL,
    patch_description TEXT,
    patch_release_date DATE NOT NULL
);

CREATE TABLE security_patches (
    id SERIAL PRIMARY KEY,
    patch_name VARCHAR(100) NOT NULL,
    patch_description TEXT,
    patch_date TIMESTAMP NOT NULL
);
