tables.table_schema:
CREATE TABLE devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(255),
    device_status VARCHAR(50),
    last_activity TIMESTAMP
);

CREATE TABLE device_data (
  id INT PRIMARY KEY,
  device_id INT,
  data VARCHAR(255),
  timestamp TIMESTAMP
);

CREATE TABLE device_data (
    id SERIAL PRIMARY KEY,
    device_id INT,
    data JSONB,
    timestamp TIMESTAMPTZ
);

CREATE TABLE triggers (
    id SERIAL PRIMARY KEY,
    trigger_name VARCHAR(255),
    trigger_description TEXT
);

CREATE TABLE alert_logs (
    id SERIAL PRIMARY KEY,
    alert_id INT,
    alert_timestamp TIMESTAMPTZ,
    alert_message TEXT
);

CREATE TABLE security_teams (
    id SERIAL PRIMARY KEY,
    team_name VARCHAR(255),
    team_email VARCHAR(255)
);

CREATE TABLE IoT_devices (
 device_id SERIAL PRIMARY KEY,
 device_name VARCHAR(50) NOT NULL,
 device_type VARCHAR(50) NOT NULL,
 last_activity TIMESTAMP
);

CREATE TABLE Activity_logs (
 log_id SERIAL PRIMARY KEY,
 device_id INT REFERENCES IoT_devices(device_id),
 activity_type VARCHAR(50) NOT NULL,
 activity_time TIMESTAMP
);

CREATE TABLE Alerts (
 alert_id SERIAL PRIMARY KEY,
 log_id INT REFERENCES Activity_logs(log_id),
 alert_type VARCHAR(50) NOT NULL,
 alert_time TIMESTAMP
);

CREATE TABLE Security_teams (
 team_id SERIAL PRIMARY KEY,
 team_name VARCHAR(50) NOT NULL,
 team_lead VARCHAR(50) NOT NULL
);

CREATE TABLE Team_assignments (
 assignment_id SERIAL PRIMARY KEY,
 team_id INT REFERENCES Security_teams(team_id),
 device_id INT REFERENCES IoT_devices(device_id)
);
