tables.table_schema:
CREATE TABLE blockchain_blocks (
   block_id INT PRIMARY KEY,
   previous_block_id INT,
   block_hash VARCHAR(64),
   block_timestamp TIMESTAMP
);

CREATE TABLE blockchain_addresses (
   address_id INT PRIMARY KEY,
   address VARCHAR(255),
   public_key VARCHAR(255),
   balance DECIMAL(18, 2)
);

CREATE TABLE block_headers (
    block_number INTEGER PRIMARY KEY,
    block_hash TEXT,
    timestamp TIMESTAMP,
    miner_address TEXT
);

CREATE TABLE block_addresses (
 address_id INT PRIMARY KEY,
 address VARCHAR(255),
 balance DECIMAL(10,2)
);

CREATE TABLE block_metadata (
 metadata_id INT PRIMARY KEY,
 block_id INT,
 previous_block_id INT,
 timestamp TIMESTAMP
);

CREATE TABLE blocks (
  block_number INT,
  miner_address VARCHAR(255),
  timestamp TIMESTAMP
);

CREATE TABLE addresses (
  address_id INT,
  address VARCHAR(255),
  balance DECIMAL(18, 6)
);

CREATE TABLE miners (
  miner_id INT,
  miner_address VARCHAR(255),
  mining_power DECIMAL(10, 2)
);

CREATE TABLE block_transactions (
    block_id INT,
    transaction_id INT,
    timestamp TIMESTAMP
);
