tables.table_schema:
CREATE TABLE user_activity_reporting (
  user_id INT,
  activity_type VARCHAR(50),
  activity_timestamp TIMESTAMP,
  accessed_database VARCHAR(100)
);

CREATE TABLE user_activity_reports (
    user_id UUID PRIMARY KEY,
    action TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE financial_access_reports (
    user_id UUID,
    access_level TEXT,
    timestamp TIMESTAMP,
    FOREIGN KEY (user_id) REFERENCES user_activity_reports(user_id)
);

CREATE TABLE user_activity (
    user_id INT,
    activity_type VARCHAR(255),
    activity_date TIMESTAMP,
    activity_description TEXT
);

CREATE TABLE financial_database_access (
    user_id INT,
    database_name VARCHAR(255),
    access_date TIMESTAMP,
    access_type VARCHAR(50)
);

CREATE TABLE audit_log (
    log_id INT,
    user_id INT,
    action_type VARCHAR(50),
    action_date TIMESTAMP
);

CREATE TABLE regulatory_compliance (
    report_id INT,
    user_id INT,
    report_type VARCHAR(50),
    report_date TIMESTAMP
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    role VARCHAR(20)
);

CREATE TABLE user_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    action VARCHAR(50),
    timestamp TIMESTAMP
);

CREATE TABLE financial_databases (
    database_id INT PRIMARY KEY,
    database_name VARCHAR(50),
    location VARCHAR(100),
    encryption_key VARCHAR(100)
);

CREATE TABLE audit_logs (
    log_id INT PRIMARY KEY,
    action VARCHAR(50),
    timestamp TIMESTAMP
);
