tables.table_schema:
CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR,
    customer_address VARCHAR,
    customer_phone VARCHAR
);

CREATE TABLE transactions (
  transaction_id UUID PRIMARY KEY,
  amount DECIMAL,
  transaction_date DATE,
  is_fraudulent BOOLEAN
);

CREATE TABLE accounts (
  account_id UUID PRIMARY KEY,
  account_holder_name VARCHAR,
  account_balance DECIMAL,
  account_type VARCHAR
);

CREATE TABLE suspicious_transactions (
  transaction_id UUID PRIMARY KEY,
  reason TEXT,
  reported_by VARCHAR,
  report_date DATE
);

CREATE TABLE accounts (
    account_id INT PRIMARY KEY,
    account_holder_name VARCHAR(255) NOT NULL,
    account_balance DECIMAL NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE transaction_logs (
    log_id INT PRIMARY KEY,
    transaction_id INT NOT NULL,
    log_message VARCHAR(255),
    log_datetime TIMESTAMP NOT NULL
);

CREATE TABLE fraud_patterns (
    pattern_id INT PRIMARY KEY,
    pattern_name VARCHAR(255) NOT NULL,
    rule_description VARCHAR(255) NOT NULL,
    is_active BOOL DEFAULT TRUE
);

CREATE TABLE Transactions (
    transaction_id INT PRIMARY KEY,
    transaction_amount FLOAT,
    transaction_date DATE,
    account_id INT
);

CREATE TABLE Accounts (
    account_id INT PRIMARY KEY,
    account_type VARCHAR(50),
    account_balance FLOAT
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(50),
    user_email VARCHAR(50)
);

CREATE TABLE FraudulentTransactions (
    transaction_id INT PRIMARY KEY,
    fraud_detection_score FLOAT,
    fraud_detection_reason TEXT
);

CREATE TABLE TransactionLogs (
    log_id INT PRIMARY KEY,
    log_timestamp TIMESTAMP,
    log_message TEXT
);
