tables.table_schema:
CREATE TABLE financial_systems (
  system_id INT PRIMARY KEY,
  system_name VARCHAR(50) NOT NULL,
  security_level INT
);

CREATE TABLE system_patches (
    system_id INT,
    patch_id INT,
    FOREIGN KEY (system_id) REFERENCES financial_systems(system_id),
    FOREIGN KEY (patch_id) REFERENCES security_patches(patch_id)
);
