tables.table_schema:
CREATE TABLE smart_contracts (
    contract_id UUID PRIMARY KEY,
    contract_name TEXT NOT NULL,
    contract_address TEXT UNIQUE
);

CREATE TABLE contract_data (
    data_id UUID PRIMARY KEY,
    contract_id UUID REFERENCES smart_contracts(contract_id),
    data TEXT NOT NULL
);

CREATE TABLE contract_owners (
    owner_id UUID PRIMARY KEY,
    contract_id UUID REFERENCES smart_contracts(contract_id),
    owner_name TEXT
);

CREATE TABLE smart_contracts (
    contract_id UUID PRIMARY KEY,
    contract_name VARCHAR(255) NOT NULL,
    contract_address VARCHAR(255) NOT NULL,
    contract_status VARCHAR(50) DEFAULT 'Active'
);

CREATE TABLE contract_events (
    event_id UUID PRIMARY KEY,
    contract_id UUID REFERENCES smart_contracts(contract_id),
    event_type VARCHAR(100) NOT NULL,
    event_data JSONB
);

CREATE TABLE contract_owners (
    owner_id UUID PRIMARY KEY,
    contract_id UUID REFERENCES smart_contracts(contract_id),
    owner_name VARCHAR(255) NOT NULL,
    owner_address VARCHAR(255) NOT NULL
);

CREATE TABLE contract_transactions (
    transaction_id UUID PRIMARY KEY,
    contract_id UUID REFERENCES smart_contracts(contract_id),
    transaction_amount DECIMAL(12, 2) NOT NULL,
    transaction_date TIMESTAMP
);

CREATE TABLE SmartContracts (
    contract_id UUID PRIMARY KEY,
    contract_name TEXT,
    contract_address TEXT,
    contract_owner TEXT
);

CREATE TABLE ContractData (
    data_id UUID PRIMARY KEY,
    contract_id UUID REFERENCES SmartContracts(contract_id),
    data_key TEXT,
    data_value TEXT
);

CREATE TABLE Transactions (
    transaction_id UUID PRIMARY KEY,
    contract_id UUID REFERENCES SmartContracts(contract_id),
    transaction_type TEXT,
    transaction_date DATE
);

CREATE TABLE Events (
    event_id UUID PRIMARY KEY,
    contract_id UUID REFERENCES SmartContracts(contract_id),
    event_type TEXT,
    event_date DATE
);

CREATE TABLE Users (
    user_id UUID PRIMARY KEY,
    username TEXT,
    email TEXT,
    role TEXT
);
