tables.table_schema:
CREATE TABLE TrendAnalysis (
    analysis_id INT PRIMARY KEY,
    market_id INT,
    analysis_date DATE,
    trend VARCHAR
);

CREATE TABLE MarketTrends (
  id INT PRIMARY KEY,
  trend_name VARCHAR(20),
  analysis_date DATE,
  description TEXT
);

CREATE TABLE Advisor (
    advisor_id INT PRIMARY KEY,
    advisor_name VARCHAR(50) NOT NULL,
    advisor_email VARCHAR(100) NOT NULL
);

CREATE TABLE MarketData (
    data_id INT PRIMARY KEY,
    advisor_id INT NOT NULL,
    market_price DECIMAL(10,2) NOT NULL,
    market_date DATE NOT NULL
);

CREATE TABLE TrendAnalysis (
    analysis_id INT PRIMARY KEY,
    data_id INT NOT NULL,
    trend_type VARCHAR(20) NOT NULL,
    trend_score DECIMAL(5,2) NOT NULL
);

CREATE TABLE Forecasting (
    forecast_id INT PRIMARY KEY,
    analysis_id INT NOT NULL,
    forecast_date DATE NOT NULL,
    forecast_value DECIMAL(10,2) NOT NULL
);

CREATE TABLE Forecast (
	id SERIAL,
	symbol VARCHAR(255),
	forecast_date DATE,
	forecast_value DECIMAL
);

CREATE TABLE Advisors (
	advisor_id SERIAL,
	advisor_name VARCHAR(255),
	advisor_specialization VARCHAR(100)
);
