tables.table_schema:
CREATE TABLE Customer_Portfolios (
    customer_id INT PRIMARY KEY,
    portfolio_name VARCHAR(50),
    total_assets DECIMAL(12, 2),
    risk_level VARCHAR(10)
);

CREATE TABLE Customer (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    customer_email VARCHAR(100)
);

CREATE TABLE Portfolio (
    portfolio_id INT PRIMARY KEY,
    customer_id INT REFERENCES Customer(customer_id),
    balance DECIMAL(10, 2),
    assets TEXT
);

CREATE TABLE Criteria (
    criteria_id INT PRIMARY KEY,
    criteria_name VARCHAR(50),
    criteria_description TEXT
);

CREATE TABLE RebalanceCriteria (
  criteria_id INT PRIMARY KEY,
  criteria_name VARCHAR(50),
  asset_type VARCHAR(20),
  threshold DECIMAL(10, 2)
);

CREATE TABLE Customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255),
    customer_email VARCHAR(255)
);

CREATE TABLE Portfolios (
    portfolio_id INT PRIMARY KEY,
    customer_id INT,
    portfolio_name VARCHAR(255),
    portfolio_value DECIMAL(10, 2)
);

CREATE TABLE Assets (
    asset_id INT PRIMARY KEY,
    asset_name VARCHAR(255),
    asset_type VARCHAR(50),
    asset_value DECIMAL(10, 2)
);

CREATE TABLE Criteria (
    criteria_id INT PRIMARY KEY,
    criteria_name VARCHAR(255),
    rebalance_threshold DECIMAL(5, 2)
);

CREATE TABLE RebalancingHistory (
    transaction_id INT PRIMARY KEY,
    portfolio_id INT,
    timestamp TIMESTAMP,
    action_taken VARCHAR(50)
);
