tables.table_schema:
CREATE TABLE customer_behavior (
    customer_id INT PRIMARY KEY,
    behavior_type VARCHAR(255),
    behavior_date TIMESTAMP,
    behavior_description TEXT
);

CREATE TABLE investment_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(255),
    risk_level VARCHAR(50),
    description TEXT
);

CREATE TABLE CustomerBehavior (
  customer_id INT PRIMARY KEY,
  behavior_type VARCHAR(50),
  timestamp TIMESTAMP
);

CREATE TABLE InvestmentStrategy (
  strategy_id INT PRIMARY KEY,
  strategy_name VARCHAR(100),
  risk_level INT,
  created_date DATE
);

CREATE TABLE RiskManagement (
  risk_id INT PRIMARY KEY,
  risk_level INT,
  risk_description TEXT
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    customer_email VARCHAR(100),
    customer_age INT
);

CREATE TABLE risk_profiles (
    profile_id INT PRIMARY KEY,
    profile_name VARCHAR(50),
    risk_score INT
);
