tables.table_schema:
CREATE TABLE compliance_documents (
    document_id INT PRIMARY KEY,
    document_type VARCHAR(30) NOT NULL,
    document_date DATE NOT NULL,
    document_content TEXT
);

CREATE TABLE regulatory_filings (
    filing_id INT PRIMARY KEY,
    advisor_id INT,
    filing_date DATE,
    document TEXT
);

CREATE TABLE advisors (
    advisor_id INT PRIMARY KEY,
    advisor_name VARCHAR(100),
    company_name VARCHAR(100),
    registration_date DATE
);

CREATE TABLE legal_standards (
    standard_id INT PRIMARY KEY,
    standard_name VARCHAR(100),
    description TEXT
);

CREATE TABLE regulatory_entities (
    entity_id INT PRIMARY KEY,
    entity_name VARCHAR(50),
    entity_type VARCHAR(50),
    jurisdiction VARCHAR(50)
);

CREATE TABLE advisor_info (
    advisor_id INT PRIMARY KEY,
    advisor_name VARCHAR(50),
    advisor_license VARCHAR(20),
    advisor_location VARCHAR(50)
);

CREATE TABLE regulatory_changes (
    change_id INT PRIMARY KEY,
    change_date DATE,
    change_description TEXT
);
