tables.table_schema:
CREATE TABLE robo_advisors ( 
  client_id INT PRIMARY KEY, 
  client_name VARCHAR(50), 
  total_assets DECIMAL(10,2), 
  risk_tolerance VARCHAR(10) 
 );

CREATE TABLE ClientPortfolio (
  client_id INT PRIMARY KEY,
  portfolio_name VARCHAR(255) NOT NULL,
  total_assets DECIMAL(10, 2) DEFAULT 0,
  allocation DATE
);

CREATE TABLE clients (
    client_id UUID PRIMARY KEY,
    client_name VARCHAR(50) NOT NULL,
    client_email VARCHAR(100) NOT NULL
);

CREATE TABLE portfolios (
    portfolio_id UUID PRIMARY KEY,
    client_id UUID REFERENCES clients(client_id) NOT NULL,
    total_value DECIMAL(10, 2) NOT NULL
);

CREATE TABLE RoboAdvisors (
    advisor_id UUID PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    company VARCHAR(50) NOT NULL
);

CREATE TABLE Clients (
    client_id UUID PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    email VARCHAR(50) NOT NULL,
    advisor_id UUID REFERENCES RoboAdvisors(advisor_id)
);

CREATE TABLE Assets (
    asset_id UUID PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    value FLOAT NOT NULL
);

CREATE TABLE Portfolios (
    portfolio_id UUID PRIMARY KEY,
    client_id UUID REFERENCES Clients(client_id)
);

CREATE TABLE PortfolioAssets (
    portfolio_id UUID REFERENCES Portfolios(portfolio_id),
    asset_id UUID REFERENCES Assets(asset_id),
    quantity INT NOT NULL
);
