CREATE TABLE cryptocurrency_exchange (
  id UUID PRIMARY KEY,
  exchange_name VARCHAR(100) NOT NULL,
  exchange_location VARCHAR(100) NOT NULL
);

CREATE TABLE cryptocurrency (
  id UUID PRIMARY KEY,
  symbol VARCHAR(10) NOT NULL,
  name VARCHAR(100) NOT NULL
);

CREATE TABLE investment_decision (
  id UUID PRIMARY KEY,
  decision_description TEXT NOT NULL,
  cryptocurrency_id UUID REFERENCES cryptocurrency(id),
  timestamp TIMESTAMP NOT NULL
);

CREATE TABLE exchanges (
  exchange_id INT,
  exchange_name VARCHAR(50),
  location VARCHAR(50),
  website_url VARCHAR(255)
);

CREATE TABLE cryptocurrencies (
  symbol VARCHAR(10),
  name VARCHAR(50),
  market_cap DECIMAL(18, 6),
  circulating_supply DECIMAL(18, 6)
);

CREATE TABLE users (
  user_id INT,
  username VARCHAR(50),
  email VARCHAR(50),
  created_at TIMESTAMP
);
