tables.table_schema:
CREATE TABLE Underwriters (
  underwriter_id INT NOT NULL,
  underwriter_name VARCHAR(255) NOT NULL,
  underwriter_region VARCHAR(50) NOT NULL,
  underwriter_experience_years INT NOT NULL
);

CREATE TABLE PolicyDocuments (
  document_id INT NOT NULL,
  policy_id INT NOT NULL,
  document_type VARCHAR(50) NOT NULL,
  document_url VARCHAR(255) NOT NULL
);

CREATE TABLE PolicyHolder (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    age INT,
    address VARCHAR(255)
);

CREATE TABLE PolicyDetails (
    detail_id INT PRIMARY KEY,
    coverage VARCHAR(255),
    deductible DECIMAL(10, 2),
    status ENUM('Active', 'Inactive')
);

CREATE TABLE Underwriter (
    underwriter_id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    specialty VARCHAR(100)
);
