tables.table_schema:
CREATE TABLE policy_management (
    policy_id INT PRIMARY KEY,
    policy_holder VARCHAR(255),
    renewal_date DATE,
    expiration_date DATE
);

CREATE TABLE policy_renewals (
    policy_id INT PRIMARY KEY,
    policy_holder VARCHAR(255) NOT NULL,
    renewal_date DATE NOT NULL
);

CREATE TABLE policy_expirations (
    policy_id INT PRIMARY KEY,
    policy_holder VARCHAR(255) NOT NULL,
    expiration_date DATE NOT NULL
);

CREATE TABLE policy_renewal (
    policy_id INT PRIMARY KEY,
    policy_number VARCHAR(255),
    renewal_date DATE,
    expiration_date DATE
);

CREATE TABLE policy_holder (
    holder_id INT PRIMARY KEY,
    full_name VARCHAR(255),
    email VARCHAR(255),
    phone_number VARCHAR(20)
);

CREATE TABLE policy_history (
    history_id INT PRIMARY KEY,
    policy_id INT,
    action VARCHAR(50),
    timestamp TIMESTAMP
);

CREATE TABLE policy_managers (
    policy_id INT PRIMARY KEY,
    policy_holder_name VARCHAR(50),
    renewal_date DATE,
    expiration_date DATE
);

CREATE TABLE policy_renewals (
    renewal_id INT PRIMARY KEY,
    policy_id INT REFERENCES policy_managers(policy_id),
    renewal_date DATE,
    renewal_amount DECIMAL
);

CREATE TABLE policy_expirations (
    expiration_id INT PRIMARY KEY,
    policy_id INT REFERENCES policy_managers(policy_id),
    expiration_date DATE
);

CREATE TABLE policy_history (
    history_id INT PRIMARY KEY,
    policy_id INT REFERENCES policy_managers(policy_id),
    event_date DATE,
    event_description TEXT
);

CREATE TABLE PolicyRenewals (
    policy_id INT PRIMARY KEY,
    policy_holder VARCHAR(255),
    renewal_date DATE,
    expiration_date DATE
);

CREATE TABLE PolicyHolders (
    holder_id INT PRIMARY KEY,
    holder_name VARCHAR(255),
    email VARCHAR(255),
    phone_number VARCHAR(20)
);

CREATE TABLE Agents (
    agent_id INT PRIMARY KEY,
    agent_name VARCHAR(255),
    commission_rate DECIMAL(5, 2)
);

CREATE TABLE Claims (
    claim_id INT PRIMARY KEY,
    policy_id INT,
    claim_date DATE,
    claim_amount DECIMAL(10, 2)
);
