tables.table_schema:
CREATE TABLE credit_risk_prediction(
  customer_id INT,
  loan_amount DECIMAL,
  repayment_history TEXT,
  credit_score INT
);

CREATE TABLE CreditScore (
    customer_id INT,
    credit_score INT,
    loan_amount DECIMAL(10, 2),
    late_payments INT
);

CREATE TABLE LoanApplications (
    application_id INT,
    customer_id INT,
    loan_amount DECIMAL(10, 2),
    approval_status VARCHAR(10)
);

CREATE TABLE RepaymentHistory (
    repayment_id INT,
    customer_id INT,
    repayment_amount DECIMAL(10, 2),
    payment_date DATE
);

CREATE TABLE RiskAssessment (
    assessment_id INT,
    customer_id INT,
    risk_score INT,
    assessment_date DATE
);

CREATE TABLE CustomerInformation (
    customer_id INT,
    full_name VARCHAR(50),
    address VARCHAR(100),
    phone_number VARCHAR(15)
);
