tables.table_schema:
CREATE TABLE financial_reporting (
    id INT PRIMARY KEY,
    report_date DATE NOT NULL,
    financial_data DECIMAL NOT NULL
);

CREATE TABLE regulatory_authorities (
    authority_id INT PRIMARY KEY,
    authority_name VARCHAR(50) NOT NULL,
    reporting_requirements TEXT
);

CREATE TABLE FinancialData ( 
  id INT PRIMARY KEY, 
  date DATE, 
  amount DECIMAL(10, 2) 
);

CREATE TABLE RegulatoryAuthorities ( 
  authority_id INT PRIMARY KEY, 
  authority_name VARCHAR(255) 
);

CREATE TABLE ReportingRegulations ( 
  regulation_id INT PRIMARY KEY, 
  regulation_name VARCHAR(255), 
  reporting_frequency VARCHAR(50) 
);

CREATE TABLE financial_reports (
 report_id UUID PRIMARY KEY,
 report_name VARCHAR(255),
 report_date DATE,
 submitted BOOLEAN
);

CREATE TABLE regulatory_authorities (
 authority_id UUID PRIMARY KEY,
 authority_name VARCHAR(255),
 address VARCHAR(255),
 contact_person VARCHAR(100)
);

CREATE TABLE report_data (
 data_id UUID PRIMARY KEY,
 report_id UUID,
 data_category VARCHAR(100),
 data_value DECIMAL
);

CREATE TABLE compliance_status (
 status_id UUID PRIMARY KEY,
 report_id UUID,
 authority_id UUID,
 status VARCHAR(50)
);

CREATE TABLE financial_data ( 
	report_id UUID PRIMARY KEY, 
	report_date DATE, 
	company_name VARCHAR, 
	total_assets FLOAT 
);

CREATE TABLE regulatory_authorities ( 
	authority_id UUID PRIMARY KEY, 
	authority_name VARCHAR, 
	authority_type VARCHAR 
);

CREATE TABLE reporting_regulations ( 
	regulation_id UUID PRIMARY KEY, 
	regulation_name VARCHAR, 
	requirement_description TEXT 
);

CREATE TABLE reported_data ( 
	report_id UUID FOREIGN KEY REFERENCES financial_data(report_id), 
	authority_id UUID FOREIGN KEY REFERENCES regulatory_authorities(authority_id), 
	regulation_id UUID FOREIGN KEY REFERENCES reporting_regulations(regulation_id), 
	reported_value FLOAT 
);

CREATE TABLE report_submission_logs ( 
	log_id UUID PRIMARY KEY, 
	report_id UUID FOREIGN KEY REFERENCES financial_data(report_id), 
	submission_timestamp TIMESTAMP 
);
