tables.table_schema:
CREATE TABLE customer_consent (
    id INT PRIMARY KEY,
    customer_id INT,
    consent_date DATE,
    consent_given BOOLEAN
);

CREATE TABLE customer_consent (
   customer_id INT PRIMARY KEY,
   consent_status VARCHAR(255),
   date_given DATE
);

CREATE TABLE consent_logs (
   log_id INT AUTO_INCREMENT PRIMARY KEY,
   customer_id INT,
   consent_status_change VARCHAR(255),
   log_date DATETIME
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(100),
    consent_date DATE
);

CREATE TABLE consent_types (
    consent_id INT PRIMARY KEY,
    consent_description VARCHAR(255)
);

CREATE TABLE customer_consent (
    customer_id INT PRIMARY KEY,
    consent_date DATE,
    consent_type VARCHAR(50),
    consent_description TEXT
);

CREATE TABLE consent_types (
    type_id SERIAL PRIMARY KEY,
    type_name VARCHAR(50) UNIQUE
);

CREATE TABLE customer_details (
    customer_id INT PRIMARY KEY,
    full_name VARCHAR(100),
    email VARCHAR(100) UNIQUE,
    phone_number VARCHAR(20)
);

CREATE TABLE customer_consent (
  customer_id INT PRIMARY KEY,
  consent_date DATE NOT NULL,
  consent_type VARCHAR(50) NOT NULL
);

CREATE TABLE consent_statistics (
  statistics_id INT PRIMARY KEY,
  consent_type VARCHAR(50) NOT NULL,
  total_count INT NOT NULL,
  approval_rate DECIMAL(5,2) NOT NULL
);
